      }}
      validationSchema={formValidationSchema}
    >
      {({
        values,
        handleChange,
        handleBlur,
        handleSubmit,
        errors,
        touched,
      }) => {
        console.log(errors);
        return (
          <form onSubmit={handleSubmit}>
            <div>
              <Box
                style={{
                  height: "auto",
                  width: "90%",
                  backgroundColor: "white",
                  marginLeft: "60px",
                  marginTop: "30px",
                  boxShadow: "  1px 4px  7px 5px #E5E5E5",
                }}
              >
                <Box style={{ display: "flex" }}>
                  <Box
                    style={{
                      flex: 2,
                      padding: "0px 20px",
                      height: "100%",
                      backgroundColor: "white",
                    }}
                  >
                    <Box
                      style={{
                        display: "flex",
                        flexDirection: "row",
                        justifyContent: "spacebetween",
                      }}
                    >
                      <Input
                        name="name"
                        value={values.name}
                        onChange={handleChange}
                        type="text"
                        placeholder="Full Name "
                        disableUnderline={true}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        style={{
                          borderRadius: "4px",
                          marginTop: "20px",
                          backgroundColor: "#F1F1F1",
                          marginRight: "20px",
                          padding: "10px",
                          border: "1px solid  #E5E5E5 ",
                        }}
                      >
                    </Input>



                      <Input
                        name="mobile"
                        value={values.mobile}
                        onChange={handleChange}
                        onBlur={handleBlur}
                        type="number"
                        placeholder="Mobile No "
                        disableUnderline={true}
                        style={{
                          borderRadius: "4px",
                          marginTop: "20px",
                          backgroundColor: "#F1F1F1",
                          marginRight: "20px",
                          padding: "10px",
                          border: "1px solid  #E5E5E5 ",
                        }}
                      >
                      </Input>
                      </Box>
                    <Typography style={{ fontSize: 12, color: "red" }}>
                    {touched.name && errors.name}
                  </Typography>
                  <Typography style={{ fontSize: 12, color: "red" }}>
                    {touched.mobile && errors.mobile}
                  </Typography>

                    <Box
                      style={{
                        display: "flex",
                        flexDirection: "row",
                        justifyContent: "spacebetween",
                      }}
                    >
                      <Input
                        name="email"
                        value={values.email}
                        onChange={handleChange}
                        type="text"
                        placeholder="Email "
                        disableUnderline={true}
                        style={{
                          borderRadius: "4px",
                          marginTop: "20px",
                          backgroundColor: "#F1F1F1",
                          marginRight: "20px",
                          padding: "10px",
                          border: "1px solid  #E5E5E5 ",
                        }}
                      >
                        hello
                      </Input>

                      <Input
                        name="age"
                        value={values.age}
                        onChange={handleChange}
                        type="number"
                        placeholder="Age "
                        disableUnderline={true}
                        style={{
                          borderRadius: "4px",
                          marginTop: "20px",
                          backgroundColor: "#F1F1F1",
                          marginRight: "20px",
                          padding: "10px",
                          border: "1px solid  #E5E5E5 ",
                        }}
                      >
                        hello
                      </Input>
                    </Box>
                    <Box
                      style={{
                        display: "flex",
                        flexDirection: "row",
                        justifyContent: "spacebetween",
                      }}
                    >
                      <Input
                        name="bloodgroup"
                        value={values.bloodgroup}
                        onChange={handleChange}
                        type="text"
                        placeholder="Blood Group"
                        disableUnderline={true}
                        style={{
                          borderRadius: "4px",
                          marginTop: "20px",
                          backgroundColor: "#F1F1F1",
                          marginRight: "20px",
                          padding: "10px",
                          border: "1px solid  #E5E5E5 ",
                        }}
                      >
                        hello
                      </Input>

                      <Input
                        name="address"
                        value={values.address}
                        onChange={handleChange}
                        type="text"
                        placeholder="Address "
                        disableUnderline={true}
                        style={{
                          borderRadius: "4px",
                          marginTop: "20px",
                          backgroundColor: "#F1F1F1",
                          marginRight: "20px",
                          padding: "10px",
                          border: "1px solid  #E5E5E5 ",
                        }}
                      >
                        hello
                      </Input>
                    </Box>
                    <Box
                      style={{
                        display: "flex",
                        flexDirection: "row",
                        justifyContent: "space-around",
                      }}
                    >
                      <Button
                        style={{
                          marginTop: "40px",
                          padding: "10px",
                          marginBottom: "20px",
                        }}
                        variant="contained"
                        color="primary"
                      >
                        Submit
                      </Button>
                      <Button
                        style={{
                          marginTop: "40px",
                          padding: "10px",
                          marginBottom: "20px",
                          backgroundColor: "#898895",
                        }}
                        variant="contained"
                        color="black"
                        backgroundColor="#898895"
                      >
                        Reset
                      </Button>
                    </Box>
                  </Box>
                  <Box
                    style={{
                      flex: 2,
                      padding: "0px 20px",
                      height: "100%",
                      backgroundColor: "#898895",
                    }}
                  ></Box>
                </Box>
              </Box>
            </div>
          </form>
        );
      }}
    </Formik>
  );
};
export default index;
