import React, { useEffect, useState } from "react";

import * as Yup from "yup";
import { Formik } from "formik";
import axios from "axios";

import {
  Box,
  Input,
  Button,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
} from "@material-ui/core";
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from '@material-ui/icons/Edit';

const formValidationSchema = Yup.object().shape({
  name: Yup.string()
    .min(2, "Too Short!")
    .max(25, "Too Long!")
    .required("Name is required"),
  mobile: Yup.string()
    .min(2, "Too Short!")
    .max(25, "Too Long!")
    .min(6, "Password has to be longer than 6 characters!")
    .required("Valid password  is required"),
  email: Yup.string()
    .min(2, "Too Short!")
    .max(25, "Too Long!")
    .required("Email is required"),
  age: Yup.string()
    .min(2, "Too Short!")
    .max(25, "Too Long!")
    .required("Age  is required"),
  bloodgroup: Yup.string()
    .min(2, "Too Short!")
    .max(25, "Too Long!")
    .required("blood group is required"),
  address: Yup.string()
    .min(2, "Too Short!")
    .max(25, "Too Long!")
    .required("Address is required"),
});

const initialValues = {
  name: "",
  mobile: "",
  email: "",
  age: "",
  bloodgroup: "",
  address: "",
};

const index = () => {
  const [students, seStudents] = useState([]);
  const [query, setQuery] =useState([]);

  // For View data

  const getData = async () => {
    console.log("from here;");
    try {
      const {
        data: {data},
      } = await axios.get("http://localhost:3000");
      // const response = await axios.get("http://localhost:3000");
      // const data = response.data.data;
      console.log(data);
      seStudents(data);
    } catch (error) {
      console.log(error);
    }
  };
    // For Add Data To a databse
  const addData = async (values) => {
    try {
      await axios.post("http://localhost:3000", values);
      getData();
    } catch (error) {
      console.log(error);
    } };

  // ************************ Delete data from the table 
  const deleteData = async (name) => {
    try {
      await axios.delete(`http://localhost:3000?name=${name}`);
      getData();
    } catch (error) {
      console.log(error);
    }
  };
  const putData =async(name)=>{
    try{
    const {
      data: {data},
    } = await axios.get("http://localhost:3000");
   setStudent(data);
    await axios.put(`http://localhost:3000?name=${name}`);
    // getData();
   }  
   catch (error) {
    console.log(error);
  }
};




  useEffect(() => {
    getData();
  }, []);

  console.log(students);

  return (
    <Formik
      initialValues={initialValues}
      onSubmit={(values) => {
        addData(values);
      }}
      validationSchema={formValidationSchema}
    >
      {({
        values,
        handleChange,
        handleBlur,
        handleSubmit,
        errors,
        touched,
      }) => {
        console.log(errors);
        return (
          <form onSubmit={handleSubmit}>
            <div>
              <Box
                style={{
                  height: "auto",
                  width: "90%",
                  backgroundColor: "white",
                  marginLeft: "60px",
                  marginTop: "30px",
                  boxShadow: "  1px 4px  7px 5px #E5E5E5",
                }}
              >
                <Box style={{ display: "flex" }}>
                  <Box
                    style={{
                      flex: 2,
                      padding: "0px 10px",
                      height: "100%",
                      backgroundColor: "white",
                    }}
                  >
                    <Box
                      style={{
                        display: "flex",
                        flexDirection: "row",
                        justifyContent: "spacebetween",
                      }}
                    >
                      <Box>
                        <Input
                          name="name"
                          value={values.name}
                          onChange={handleChange}
                          type="text"
                          placeholder="Full Name "
                          disableUnderline={true}
                          onChange={handleChange}
                          onBlur={handleBlur}
                          style={{
                            borderRadius: "4px",
                            marginTop: "20px",
                            backgroundColor: "#F1F1F1",
                            marginRight: "20px",
                            padding: "10px",
                            border: "1px solid  #E5E5E5 ",
                          }}
                        ></Input>
                        <Typography style={{ fontSize: 12, color: "red" }}>
                          {touched.name && errors.name}
                        </Typography>
                      </Box>

                      <Box>
                        <Input
                          name="mobile"
                          value={values.mobile}
                          onChange={handleChange}
                          onBlur={handleBlur}
                          type="number"
                          placeholder="Mobile No "
                          disableUnderline={true}
                          style={{
                            borderRadius: "4px",
                            marginTop: "20px",
                            backgroundColor: "#F1F1F1",
                            marginRight: "20px",
                            padding: "10px",
                            border: "1px solid  #E5E5E5 ",
                          }}
                        ></Input>
                        <Typography style={{ fontSize: 12, color: "red" }}>
                          {touched.mobile && errors.mobile}
                        </Typography>
                      </Box>
                    </Box>

                    <Box
                      style={{
                        display: "flex",
                        flexDirection: "row",
                        justifyContent: "spacebetween",
                      }}
                    >
                      <Box>
                        <Input
                          name="email"
                          value={values.email}
                          onChange={handleChange}
                          onBlur={handleBlur}
                          type="text"
                          placeholder="Email "
                          disableUnderline={true}
                          style={{
                            borderRadius: "4px",
                            marginTop: "20px",
                            backgroundColor: "#F1F1F1",
                            marginRight: "20px",
                            padding: "10px",
                            border: "1px solid  #E5E5E5 ",
                          }}
                        ></Input>
